mapper.update:
update charity
title = #{title}, content = #{content}, amount = #{amount}, cAmount = #{cAmount}, sDate = #{sDate}, eDate = #{eDate}, category = #{category}, confirm = #{confirm}, aId = #{aId}, addr = #{addr}, id = #{id},
where cNum = #{cNum}

choose.when:
select * from ( select * from charity
)aa,( select (edate-(sysdate + (1/24)*9))cal, cNum from charity
)bb where aa.cNum = bb.cNum
order by cal asc

				select bb.* from (
					select * from (
    					select aa.* from (
     						select count(c.cNum) cnt, c.cNum cn from (
       							select * from charity

      						) c, crecommend cr where c.cNum = cr.cNum group by c.cNum
    					)aa
    				order by cnt desc, cn desc)
			)aa, (select * from charity )bb
			where aa.cn = bb.cNum(+) order by cnt

choose.otherwise:
select * from (
	select aa.CNUM cnum, aa.TITLE title, aa.CONTENT content,
	aa.AMOUNT amount, aa.CAMOUNT camount, to_char(aa.SDATE, 'yy-mm-dd') sdate,
	to_char(aa.EDATE, 'yy-mm-dd') edate, ceil(edate - (sysdate + (1/24)*9)) dday,
	aa.CATEGORY category, aa.CONFIRM confirm,
	aa.AID aid, aa.ADDR addr, aa.REGDATE regdate, aa.ID id, aa.HIT hit, aa.CPRICE cprice, rownum rnum from (
		select * from charity





	)aa
) where rnum >=#{startRow} and rnum <=#{endRow}

mapper.select:
select * from (
	select aa.*, rownum rnum from(
		select * from charity
		where sdate < sysdate and edate > sysdate
		and (edate-(sysdate + (1/24)*9)) = (select min(edate-(sysdate + (1/24)*9)) from charity

		) order by cNum desc
							)aa
) where rnum = 1

select * from (
	select aa.*, rownum rnum from (
		select * from charity

		and ((sysdate + (1/24)*9) - sdate) = (select max((sysdate + (1/24)*9) - sdate) cNum from charity


					) order by cNum desc
								)aa
) where rnum =1

			select * from (
				select aa.*, rownum rnum from (
					select * from charity

        			order by cNum desc
   				 )aa
			where hit = (select max(hit) from charity
			)
			) where rnum = 1

			select * from (
				select aa.*, rownum rnum from (
					select * from charity

        			order by cNum desc
   				 )aa
			where cAmount = (select max(cAmount) from charity
			)
			) where rnum = 1
