choose.when:
select * from ( select * from funding
)aa,( select (edate-sysdate)cal, fNum from funding
)bb where aa.fNum = bb.fNum
order by cal asc

select bb.* from ( select * from ( select aa.* from ( select count(f.fNum) cnt, f.fNum fn from ( select * from funding
) f, frecommend fr where f.fNum = fr.fNum group by f.fNum )aa order by cnt desc, fn desc) )aa, (select * from funding )bb where aa.fn = bb.fNum(+)
order by cnt

mapper.select:
select * from(
	select f.*, rownum rnum from funding f


	and ceil(edate-sysdate) = (  select ceil(min(edate-sysdate)) from funding

	)
)where rnum = 1

			select * from (
				select aa.*, rownum rnum from (
					select * from funding
					where sdate > sysdate and edate > sysdate 
					and (sysdate - sdate) = (select max(sysdate - sdate) fNum from funding
						where sdate > sysdate and edate > sysdate and confirm = 'y'
                        ) order by fNum desc
											)aa
			) where rnum = 1

			select * from (
				select aa.*, rownum rnum from (
					select * from funding

        			order by fNum desc
   				 )aa
			where hit = (select max(hit) from funding

				)
			) where rnum = 1

			select * from (
				select aa.*, rownum rnum from (
					select * from funding

        			order by fNum desc
   				 )aa
			where camout = (select max(camout) from funding

				)
			) where rnum = 1
